import{_ as a,c as e,o as t,a2 as l}from"./chunks/framework.lL4UzoDo.js";const r="/wcdocs/assets/price.jxRHp6PV.png",i="/wcdocs/assets/ai%E8%AF%AD%E9%9F%B3%E6%9C%BA%E5%99%A8%E4%BA%BA%E6%9E%84%E5%BB%BA%E6%B5%81%E7%A8%8B.DREW065g.png",o="/wcdocs/assets/gpt3.5.BOrKJv9l.png",f=JSON.parse('{"title":"ChatGPT","description":"","frontmatter":{},"headers":[],"relativePath":"AIGC/OpenAI之GPT3.5分享.md","filePath":"AIGC/OpenAI之GPT3.5分享.md","lastUpdated":1684134600000}'),n={name:"AIGC/OpenAI之GPT3.5分享.md"},h=l('<h1 id="chatgpt" tabindex="-1">ChatGPT <a class="header-anchor" href="#chatgpt" aria-label="Permalink to &quot;ChatGPT&quot;">​</a></h1><h2 id="基本介绍" tabindex="-1">基本介绍 <a class="header-anchor" href="#基本介绍" aria-label="Permalink to &quot;基本介绍&quot;">​</a></h2><blockquote><p>ChatGPT（全名：Chat Generative Pre-trained Transformer），美国 OpenAI 研发的聊天机器人程序。ChatGPT 是人工智能技术驱动的自然语言处理工具，它能够通过理解和学习人类的语言来进行对话，还能根据聊天的上下文进行互动，真正像人类一样来聊天交流，甚至能完成撰写邮件、视频脚本、文案、翻译、代码，写论文等任务。</p></blockquote><p>“ChatGPT 是一种基于人工智能技术的聊天机器人，它可以与人类进行自然语言交互，回答问题、提供建议、进行闲聊等。它的核心技术是 GPT（Generative Pre-trained Transformer），这是一种深度学习模型，可以通过大量的文本数据进行训练，从而学习到自然语言的语法、语义和上下文信息，从而能够生成自然流畅的语言。</p><p>ChatGPT 可以应用于各种场景，例如客服、教育、娱乐等，可以帮助人们解决问题、获取信息、提高学习效率、缓解压力等。与传统的聊天机器人相比，ChatGPT 更加智能、自然、灵活，可以更好地模拟人类的语言交互，提供更好的用户体验。</p><p>总之，ChatGPT 是一种非常有用的技术，可以帮助人们更好地与计算机进行交互，提高工作效率和生活质量。” - By ChatGPT 3.5</p><h2 id="应用场景" tabindex="-1">应用场景 <a class="header-anchor" href="#应用场景" aria-label="Permalink to &quot;应用场景&quot;">​</a></h2><ul><li>高效的搜索引擎（整合了 ChatGPT 的 Bing 浏览器 和整合了 Bard 的 chrome 浏览器）</li><li>文档资料索引库</li><li>QA 客服</li><li>角色模拟</li><li>虚拟主播的大脑</li><li>日常工作生活辅助</li><li>写代码</li><li>写脚本</li><li>...</li></ul><h2 id="gpt3-5-使用限制" tabindex="-1">GPT3.5 使用限制 <a class="header-anchor" href="#gpt3-5-使用限制" aria-label="Permalink to &quot;GPT3.5 使用限制&quot;">​</a></h2><blockquote><p>GPT3.5-turbo 是仅次于 GPT-4 的 LLM 模型。</p></blockquote><ul><li>最大输入/输出文本：4096 tokens</li><li>价格：$0.002 / 1K tokens</li></ul><p><img src="'+r+'" alt="GPT3.5 max-tokens"></p><h2 id="gpt3-5-接入流程-基于-nodejs" tabindex="-1">GPT3.5 接入流程（基于 NodeJS） <a class="header-anchor" href="#gpt3-5-接入流程-基于-nodejs" aria-label="Permalink to &quot;GPT3.5 接入流程（基于 NodeJS）&quot;">​</a></h2><ul><li>在 OpenAI 开放平台获取 sk 密钥（API 收费，需要境外 VISA 卡）</li><li>调用 chat 接口，指定模型为 gpt-3.5-turbo</li><li>处理 GPT 的回答：一次性/流式返回对话数据给前端展示</li><li>附加：结合 TTS（文本转语音）实现语音播报</li></ul><p><img src="'+i+'" alt="AI聊天机器人构建流程"></p><p><img src="'+o+'" alt="GPT3.5 max-tokens"></p><h2 id="如何像模型提问" tabindex="-1">如何像模型提问？ <a class="header-anchor" href="#如何像模型提问" aria-label="Permalink to &quot;如何像模型提问？&quot;">​</a></h2><p><a href="/wcdocs/AIGC/better_prompt.html">如何写提示词</a></p><h2 id="demo-演示" tabindex="-1">DEMO 演示 <a class="header-anchor" href="#demo-演示" aria-label="Permalink to &quot;DEMO 演示&quot;">​</a></h2><ul><li><a href="https://testm.ninainfo.cn/h5/gpt/chat.html" target="_blank" rel="noreferrer">发条鸭 ChatGPT</a></li><li><a href="https://testm.ninainfo.cn/ai/#/coolduck" target="_blank" rel="noreferrer">发条鸭 AI 小助手</a></li><li><a href="https://testm.ninainfo.cn/ai/#/qaserver" target="_blank" rel="noreferrer">发条鸭 3D 客服</a></li><li><a href="https://testm.ninainfo.cn/ai/#/qatts" target="_blank" rel="noreferrer">发条鸭文本转语音</a></li><li><a href="https://live.bilibili.com/h5/6599764" target="_blank" rel="noreferrer">哔哩哔哩直播</a></li></ul><h2 id="短期限制" tabindex="-1">短期限制 <a class="header-anchor" href="#短期限制" aria-label="Permalink to &quot;短期限制&quot;">​</a></h2><ul><li>国家限制：openai 接口服务不对中国开放，需要代理访问</li><li>支付限制：需要国外的 VISA 卡作为支付方式</li><li>当前账号体验到期后（6.1），无法继续使用 API 接口</li></ul><h2 id="衍生" tabindex="-1">衍生 <a class="header-anchor" href="#衍生" aria-label="Permalink to &quot;衍生&quot;">​</a></h2><ul><li>B 站的 ChatGPT+数字人直播</li></ul><p>👀 持续关注...</p>',25),s=[h];function c(p,d,P,_,u,m){return t(),e("div",null,s)}const G=a(n,[["render",c]]);export{f as __pageData,G as default};
