import{_ as e,c as a,o,a2 as t}from"./chunks/framework.lL4UzoDo.js";const f=JSON.parse('{"title":"fs 模块：如何知道目录内文件已经全部遍历了？","description":"","frontmatter":{},"headers":[],"relativePath":"Node相关/文件遍历结束.md","filePath":"Node相关/文件遍历结束.md","lastUpdated":1678631967000}'),i={name:"Node相关/文件遍历结束.md"},r=t('<h1 id="fs-模块-如何知道目录内文件已经全部遍历了" tabindex="-1">fs 模块：如何知道目录内文件已经全部遍历了？ <a class="header-anchor" href="#fs-模块-如何知道目录内文件已经全部遍历了" aria-label="Permalink to &quot;fs 模块：如何知道目录内文件已经全部遍历了？&quot;">​</a></h1><h2 id="需求" tabindex="-1">需求 <a class="header-anchor" href="#需求" aria-label="Permalink to &quot;需求&quot;">​</a></h2><p>提交软件著作权申请需要的软件源代码一份。</p><h2 id="问题分析" tabindex="-1">问题分析 <a class="header-anchor" href="#问题分析" aria-label="Permalink to &quot;问题分析&quot;">​</a></h2><p>读取项目目录，过滤某些文件夹（如：.git，.vscode 等），只提取指定类型的文件的内容（如：js，wxml，wxss）。 即把某项目下指定类型的代码提取出来，写入到同一个文件中。</p><h2 id="封装满足以下特点的工具函数" tabindex="-1">封装满足以下特点的工具函数： <a class="header-anchor" href="#封装满足以下特点的工具函数" aria-label="Permalink to &quot;封装满足以下特点的工具函数：&quot;">​</a></h2><ul><li>可自动扫描指定路径下的所有文件及文件夹</li><li>提供指定过滤（不扫描）某些文件夹的参数 <code>ignoreDirs</code></li><li>提供指定需要的文件类型的参数 <code>allowExts</code></li><li>提供读取到文件的监听事件 <code>onFile</code></li><li>提供读取路径失败的监听事件 <code>onError</code></li><li>提供当指定路径下无可扫描文件（扫描结束）的监听事件，方法内部扫描过程是同步执行的 <code>onComplete</code></li><li>函数本身只提供指定目录扫码任务，不含文件本身的读写操作</li></ul><p><a href="https://mp.weixin.qq.com/s?__biz=MzIwNTEyMTc0Ng==&amp;mid=2648036612&amp;idx=1&amp;sn=4f0e326970132d5d55b59683b802e437&amp;chksm=8f14c4b9b8634daf618ea25c19caeb5e1b02b844eace966fb3250d16881804381234276ba17c&amp;scene=178&amp;cur_album_id=2283348312600821768#rd" target="_blank" rel="noreferrer">具体看我的公众号文章吧（my判定weigui）！</a></p>',8),l=[r];function d(c,s,n,_,h,m){return o(),a("div",null,l)}const b=e(i,[["render",d]]);export{f as __pageData,b as default};
